package com.java.javabasics;

public class Primenumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,n=2,flag = 0,m;
		m = n/2;
		
		if(n<=0 || n==1) {
			System.out.println(n + " = it is not prime number");
		}
		
		for(i=2; i<=m; i++) {
			System.out.println(n + " = it is not prime number");
		}
		if(flag==0)   
			 System.out.println(n+" is prime number"); 

	}

}
