package com.loop.javabasics;

public class Loopdemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Even and Odd number starts from 1 to 10 using do-while loop

		int i = 2;
		do {
			System.out.println("Even number is :" + i);
			i = i + 2;
		} while (i <= 10);

		System.out.println("###############################");

		int j = 1;
		do {
			System.out.println("Odd number is :" + j);
			j = j + 2;
		} while (j <= 10);

	}

}
