package com.loop.javabasics;

public class Loopdemo5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// using while loop

		// ascending order 50 to 55
		int i = 50;
		while (i <= 55) {
			System.out.println("ascending order " + i);
			i++;
		}

		System.out.println("################");

		// Descending order 55 to 50
		int j = 55;
		while (j >= 50) {
			System.out.println("descending order " + j);
			j--;
		}

	}

}
