package com.loop.javabasics;

public class Loopdemo6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// do while loop

		// ascending order 50 to 55
		int j = 50;
		do {
			System.out.println("ascending order " + j);
			j++;
		} while (j <= 55);

		// Descending order 55 to 50
		int k = 55;
		do {
			System.out.println("descending order " + k);
			k--;
		} while (k >= 50);
		
		
	}

}
